package Strings;

public class StringLowerCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "Ramesh";
		String s2 = "SHAM";
		String s3 = "JAyEsH";
		System.out.println(s1.toLowerCase());
		System.out.println(s2.toLowerCase());
		System.out.println(s3.toLowerCase());
	}
}