package Strings;

public class StringUpperCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "Ramesh";
		String s2 = "sham";
		String s3 = "JAyEsH";
		System.out.println(s1.toUpperCase());
		System.out.println(s2.toUpperCase());
		System.out.println(s3.toUpperCase());
	}
}